  var menuExtra = document.getElementById('[restaurantName]MenuExtra');
  var toggleBtn = document.getElementById('[restaurantName]MenuToggle');
  if (menuExtra.style.display === 'none') {
    menuExtra.style.display = 'block';
    toggleBtn.textContent = 'Show Less ‚ñ≤';
  } else {
    menuExtra.style.display = 'none';
    toggleBtn.textContent = 'Show More Menu Items ‚ñº';
  }
}
